select r.ID, r.FCreateTime, r.FCreateUserId, r.FFreezed, r.FDeleted, r.FChecked, r.FGUID, r.FType, r.FWidth, r.FHeight, r.FPath, r.FName, r.FFileFormat, r.FScrollType, r.FSpeed, r.FBackColor, r.FFontColor, r.FDefinition, r.FBranchId, ca.FContractId,ca.ID contractAdvId, ca.FAdvId fadvclassid, car.FOriginalResourceId,car.FIsOriginalResource, car.ID carId, u.FName userName,b.FName branch,a.ftype temp,a.FPositionId, c.FTitle contractName from t_contract_adv_resource car LEFT JOIN t_contract_adv ca ON car.FContractAdvId = ca.ID
LEFT JOIN t_resource r ON car.FOriginalResourceId = r.id
LEFT JOIN t_resource r ON car.FResourceId = r.id
LEFT JOIN t_contract c ON c.id = ca.FContractId LEFT JOIN t_user u ON u.id = r.FCreateUserId LEFT JOIN t_branch b ON b.id = r.FBranchid LEFT JOIN t_adv a ON a.id = ca.FAdvId
r.FType = #{r.ftype,jdbcType=CHAR}
AND r.FBranchId = #{r.fbranchid,jdbcType=BIGINT}
AND car.FOriginalResourceId IS NOT NULL
AND ca.FAdvId = #{r.fadvclassid,jdbcType=BIGINT}
AND c.id = #{r.fcontractid,jdbcType=BIGINT}
AND r.FChecked = #{r.fchecked,jdbcType=CHAR}
AND r.FName LINK '%${r.queryKeyWord}%'
AND r.FCreateTime BETEEN #{r.queryStartTime,jdbcType=VARCHAR} AND #{r.queryEndTime,jdbcType=VARCHAR}
order by r.ID DESC
limit #{r.start,jdbcType=INTEGER},#{r.limit,jdbcType=INTEGER}

    select count(*)
    from t_contract_adv_resource car
    LEFT JOIN t_contract_adv ca ON ca.ID = car.FContractAdvId
    
