public class VersionAdvResponse {
	

	private Long branchid;//分公司
	
	private Long rvid;//版本索引
	
	private String definition; //解析度

	public VersionAdvResponse() {
		super();
		// TODO Auto-generated constructor stub
	}
	public VersionAdvResponse(Long branchid, String definition) {
		super();
		this.branchid = branchid;
		this.definition = definition;
	}
	public VersionAdvResponse(Long branchid, Long rvid, String definition) {
		super();
		this.branchid = branchid;
		this.rvid = rvid;
		this.definition = definition;
	}
	public Long getBranchid() {
		return branchid;
	}

	public void setBranchid(Long branchid) {
		this.branchid = branchid;
	}

	public Long getRvid() {
		return rvid;
	}

	public void setRvid(Long rvid) {
		this.rvid = rvid;
	}

	public String getDefinition() {
		return definition;
	}

	public void setDefinition(String definition) {
		this.definition = definition;
	}
}